for(var button=document.querySelectorAll(".js-modal"),modal=document.querySelector(".modal"),overlay=document.querySelector(".modal__overlay"),i=0;i<button.length;i++)button[i].addEventListener("click",function(o){o.preventDefault(),modal.classList.add("modal--show")});overlay.addEventListener("click",function(){modal.classList.contains("modal--show")&&modal.classList.remove("modal--show")}),window.addEventListener("keydown",function(o){27===o.keyCode&&modal.classList.contains("modal--show")&&modal.classList.remove("modal--show")});